from codewars.kyu2.morse_code_3 import decodeBitsAdvanced, decodeMorse
from nose.tools import assert_equal

def test_hey_jude():
    check_message(
        "0000000011011010011100000110000001111110100111110011111100000000000111011111111011111011111000000101100011111100000111110011101100000100000",
        ".... . -.--   .--- ..- -.. .",
        "HEY JUDE"
    )

def test_long_message():
    check_message(
        "",
        "- .... .   --.- ..- .. -.-. -.-   -... .-. --- .-- -.   ..-. --- -..-   .--- ..- -- .--. ...   --- ...- . .-.   - .... .   .-.. .- --.. -.--   -.. --- --",
        "THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG"
    )

def test_special_characters():
    check_message(
        "",
        "...---... -.-.--   - .... .   --.- ..- .. -.-. -.-   -... .-. --- .-- -.   ..-. --- -..-   .--- ..- -- .--. ...   --- ...- . .-.   - .... .   .-.. .- --.. -.--   -.. --- --. .-.-.-",
        "SOS! THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG."
    )

def test_titanic():
    check_message(
        "",
        "TODO",
        "MGY CQD CQD SOS TITANIC POSITION 41.44 N 50.24 W. REQUIRE IMMEDIATE ASSISTANCE. COME AT ONCE. WE STRUCK AN ICEBERG. SINKING"
    )

def test_empty_message():
    yield check_message, "0", "", ""
    yield check_message, "00000000000", "", ""

def test_short_message():
    yield check_message, "1", ".", "E"
    yield check_message, "101", "..", "I"
    
    yield check_message, "1001", ". .", "EE"
    yield check_message, "10001", ". .", "EE"
    yield check_message, "100001", ". .", "EE"
    
    yield check_message, "10000001", ". .", "E E"
    yield check_message, "100000001", ". .", "E E"
    yield check_message, "1000000001", ". .", "E E"
    yield check_message, "10000000001", ". .", "E E"

    yield check_message, "10111", ".-", "A"
    yield check_message, "1110111", "--", "M"
    yield check_message, "110011", "..", "I"
    yield check_message, "111000111", "..", "I"
    yield check_message, "111110000011111", "..", "I"
    yield check_message, "11111100111111", "--", "M"

    yield check_message, "111", ".", "E"
    yield check_message, "1111111", ".", "E"

def test_extra_zeroes():
    yield check_message, "010", ".", "E"
    yield check_message, "01110", ".", "E"
    yield check_message, "0000000111000", ".", "E"

def check_message(bits, expectedEncoded, expectedDecoded):
    encoded = decodeBitsAdvanced(bits)
    decoded = decodeMorse(encoded)
    assert_equal(decoded, expectedDecoded)